package controller;import model.IMap;import model.IModel;import view.IView;import java.io.IOException;import java.sql.SQLException;/** * <h1>The Class ControllerFacade provides a facade of the Controller component.</h1> * * @author Jean-Aymeric DIET jadiet@cesi.fr * @version 1.0 */public class ControllerFacade implements IController {    /**     * The view.     */    private final IView view;    /**     * The model.     */    private final IModel model;    private Build build;    /**     * Instantiates a new controller facade.     *     * @param view  the view     * @param model the model     */    public ControllerFacade(final IView view, final IModel model) throws IOException {        this.view = view;        this.model = model;    }    /**     * Start.     *     * @throws SQLException the SQL exception     */    public void start() throws IOException {        build = new Build(view, model);        MainController main = new MainController(model);        main.test();        build = new Build(view, model);    }    @Override    public IMap getTheMap() {        return null;    }}